[3, u'old_main', '2007-06-22 09:48:29', '2007-06-22 09:48:29', 5, 5, u"clone of 'main'", u'/* ???? Farben:\r\n\r\n#C9C573;\r\n#B1B08B;\r\n#FFFFDB;\r\n\r\n*/\r\n\r\n* { color: #000000 }\r\nbody {\r\n  margin:0;\r\n  padding:0;\r\n  padding-left:15em;\r\n  padding-top:120px;\r\n\r\n  font-family: tahoma, arial, sans-serif;\r\n  color: #000;\r\n  font-size: 0.9em;\r\n  background-color: #C9C573;\r\n}\r\n\r\nbody > #sidebar, body > #headline, body > #adsense {\r\n  /* Kopf und Navi feststehend machen */\r\n  position:fixed;\r\n}\r\n\r\nhtml, body {\r\n  margin: 5px;\r\n  padding: 0;\r\n}\r\ntextarea {\r\n  background-color: #FFFFF5;\r\n}\r\n#page_msg, pre, .code, .SourceCode, fieldset legend {\r\n  font-family: Courier New,Courier,monospace,mono;\r\n  background-color: #FAFAFD;\r\n  color: #000;\r\n  padding: 10px;\r\n  border: 1px solid #C9C573;\r\n  font-size: 0.8em;\r\n}\r\n.SourceCode {\r\n  white-space:nowrap;\r\n}\r\nfieldset legend {\r\n  font-size: 1em;\r\n  padding: 3px;\r\n}\r\n#page_msg {\r\n  border-color: #F00;\r\n  color: #A00;\r\n}\r\n\r\n/* -----------------------------------------------------\r\n?berschrift ?ber die gesammte Seite\r\n----------------------------------------------------- */\r\n#headline {\r\n  position:absolute;\r\n  width: 100%;\r\n  left:0em;\r\n  top:0em;\r\n  z-index:3;\r\n\r\n  margin:0;\r\n  padding:0;\r\n\r\n  white-space:pre;\r\n  background-color:inherit;\r\n  overflow:hidden;\r\n}\r\n#headline h2 {\r\n  font-size: 2em;\r\n  margin: 0.5em;\r\n  padding-left: 1.5em;\r\n  color: #FFF;\r\n}\r\n\r\n/* -----------------------------------------------------\r\ngoogle AdSense\r\n----------------------------------------------------- */\r\n\r\n#adsense {\r\n  position:absolute;\r\n  top: 0px;\r\n  right: 3px;\r\n  z-index:99;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nMenu\r\n----------------------------------------------------- */\r\n#sidebar {\r\n  position:absolute;\r\n  top: 100px;\r\n  left: 3px;\r\n  width: 17em;\r\n  height: auto;\r\n  z-index: 10;\r\n\r\n  background-color: #FFFFDB;\r\n  border: 1px solid #B1B08B;\r\n  font-size: 0.9em;\r\n  color: #000;\r\n\r\n  text-align:left;\r\n\r\n  padding: 0em;\r\n  padding-top: 1em;\r\n  margin: 0px;\r\n}\r\n#sidebar a {\r\n  text-decoration:none;\r\n  margin: 0px;\r\n  padding: 0px;\r\n  padding-left: 0.5em;\r\n  padding-right: 0.5em;\r\n}\r\n#sidebar a:hover, #sidebar a .current {\r\n  /* hover + Aktuell angeklickter Men?punkt */\r\n  background-color: #C9C573;\r\n  color: #FFF;\r\n  margin: 0px;\r\n  padding: 0px;\r\n  padding-left: 0.5em;\r\n  padding-right: 0.5em;\r\n}\r\n#sidebar ul {\r\n  margin: 1.5em;\r\n  margin-right: 0px;\r\n  margin-top: 0.2em;\r\n  margin-bottom: 1.5em;\r\n\r\n  padding: 0px;\r\n\r\n  list-style-type: none;\r\n  border:0px;\r\n}\r\n#sidebar li {\r\n  white-space: nowrap;\r\n  margin: 0px;\r\n  margin-right: 0px;\r\n  padding: 0px;\r\n}\r\n#sidebar img {\r\n  margin: 5px 0px 5px 5px;\r\n  padding: 5px 0px 5px 5px;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nCMS Page\r\n----------------------------------------------------- */\r\n#main-content {\r\n  /* Rahmen f?r main-content + footer_nav */\r\n  position:absolute;\r\n  top: 60px;\r\n  left: 12em;\r\n  min-width: 50em;\r\n  min-height: 600px;\r\n  z-index: 1;\r\n  margin: 5px;\r\n  padding: 20px;\r\n  padding-left: 6em;\r\n  background-color: #FFFFE5;\r\n  border:1px solid black;\r\n}\r\n#main-content h2 {\r\n  border-bottom: 1px solid #000;\r\n}\r\n#nav_footer, #nav_link {\r\n  font-size: 0.6em;\r\n}\r\n#nav_footer, #nav_footer a, #nav_link, #nav_link a {\r\n  border-top: 1px solid #B1B08B;\r\n  color: #B1B08B;\r\n  text-decoration: none;\r\n  text-align: right;\r\n}\r\n#nav_link, #nav_link a {\r\n  text-align: left;\r\n  border: 0px;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nAdmin Menu\r\n----------------------------------------------------- */\r\n.adminmenu {\r\npadding: 5px;\r\nbackground-color: #C9C573;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nLinks aus dem breadcrumbs-Plugin\r\n----------------------------------------------------- */\r\n#breadcrumbs {\r\nwidth: 80%;\r\nmargin:1em auto;\r\ntext-align:left;\r\nmax-width: 1024px;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nZusatzmodule\r\n----------------------------------------------------- */\r\n.Gallery ul {\r\ntext-align: center;\r\nmargin: 0px;\r\npadding: 0px;\r\n}\r\n.Gallery li {\r\nfloat: left;\r\nborder: 1px solid #EAECFF;\r\nheight: 150px;\r\nwidth: auto;\r\ntext-align: center;\r\nmargin: 5px;\r\npadding: 5px;\r\nlist-style-type: none;\r\nbackground-color: #EDF1FF;\r\n}\r\n.clear {\r\nclear: both;\r\nmargin: 5px;\r\npadding: 5px;\r\nborder: none;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nEdit Look\r\n----------------------------------------------------- */\r\n#edit_style_select, #edit_template_select {\r\nborder-spacing: 0.5em;\r\n}\r\n#edit_style_select .name, #edit_template_select .name {\r\nfont-weight:bold\r\n\r\n}\r\n#edit_style_select .description, #edit_template_select .description {\r\nfont-style: italic;\r\n}\r\n#page_edit_preview {\r\nborder: 1px solid #C9C573;\r\n}\r\n.resize_buttons a {\r\ntext-decoration:none;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nPage Edit\r\n----------------------------------------------------- */\r\n#page_content {\r\nwidth: 100%;\r\n}\r\n\r\n\r\n/* -----------------------------------------------------\r\nsearch\r\n----------------------------------------------------- */\r\n#search_form input, #search_form button {\r\nfont-size: 0.9em;\r\nborder: 1px solid #444;\r\npadding: 1px;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nlucidFunction:RSS\r\n----------------------------------------------------- */\r\n.RSS {\r\nmargin: 1em;\r\npadding: 1em;\r\nborder: 1px solid;\r\n}\r\n.RSS li {\r\nlist-style-type: none;\r\n}\r\n.RSS h1 {\r\nfont-size: 1em;\r\nfont-weight: bold;\r\nmargin: 0px;\r\n}\r\n\r\n/* -----------------------------------------------------\r\nresize buttons for textarea\r\n----------------------------------------------------- */\r\n#resize_buttons * {\r\ntext-decoration:none;\r\nmargin: 0px;\r\npadding: 0.1em 2em 0.1em 2em;\r\nfont-family:Verdana;\r\nfont-weight:bold;\r\nline-height:2em;\r\n}\r\n#resize_buttons a:link {\r\ncolor: #000;\r\nbackground-color: #f5f5f5;\r\nborder: 1px solid;\r\nborder-color: #ddd #555 #555 #ddd;\r\n}\r\n#resize_buttons a:hover{\r\ncolor: #fff;\r\nbackground-color: #aaa;\r\nborder-color: #555 #ddd #ddd #555;\r\n}\r\n\r\n/* ----------------------------------------------------------------------------\r\nPyKleur Sourcecode highlighter\r\n------------------------------------------------------------------------------- */\r\nfieldset.syntax, fieldset.syntax * {\r\n  background-color: white;\r\n  padding: 0;\r\n  border: 0;\r\n  font-size: 1em;\r\n}\r\nfieldset.syntax, legend.syntax {\r\n  padding: 1px;\r\n  border: 1px solid #d0d0d0;\r\n}\r\nlegend.syntax {\r\n  /* fieldset legend, the sourcecode type */\r\n  font-family: Courier New,Courier,monospace,mono;\r\n  margin-left: 5px;\r\n  background-color: white;\r\n  color: black;\r\n  font-size: 0.8em;\r\n  padding: 2px;\r\n}\r\ndiv.highlight, td.linenos pre, td.code pre {\r\n  padding: 0px;\r\n  margin: 0px;\r\n}\r\ntd.linenos pre {\r\n  /* line number block */\r\n  background-color: #f5f5f5;\r\n  color: #aaa;\r\n  padding: 0px 5px 0px 1em;\r\n}\r\n\r\n/* automatic generatet! Look at PyLucid/system/page_parser.py */\r\n.syntax .c   { color: #60a0b0; font-style: italic  }  /* Comment */\r\n.syntax .err { border: 1px solid #FF0000           }  /* Error */\r\n.syntax .k   { color: #007020; font-weight: bold   }  /* Keyword */\r\n.syntax .o   { color: #666666                      }  /* Operator */\r\n.syntax .cp  { color: #007020                      }  /* Comment.Preproc */\r\n.syntax .gd  { color: #A00000                      }  /* Generic.Deleted */\r\n.syntax .ge  { font-style: italic                  }  /* Generic.Emph */\r\n.syntax .gr  { color: #FF0000                      }  /* Generic.Error */\r\n.syntax .gh  { color: #000080; font-weight: bold   }  /* Generic.Heading */\r\n.syntax .gi  { color: #00A000                      }  /* Generic.Inserted */\r\n.syntax .go  { color: #808080                      }  /* Generic.Output */\r\n.syntax .gp  { color: #c65d09; font-weight: bold   }  /* Generic.Prompt */\r\n.syntax .gs  { font-weight: bold                   }  /* Generic.Strong */\r\n.syntax .gu  { color: #800080; font-weight: bold   }  /* Generic.Subheading */\r\n.syntax .gt  { color: #0040D0                      }  /* Generic.Traceback */\r\n.syntax .kp  { color: #007020                      }  /* Keyword.Pseudo */\r\n.syntax .kt  { color: #007020; font-weight: bold   }  /* Keyword.Type */\r\n.syntax .m   { color: #40a070                      }  /* Literal.Number */\r\n.syntax .s   { color: #4070a0                      }  /* Literal.String */\r\n.syntax .na  { color: #4070a0                      }  /* Name.Attribute */\r\n.syntax .nb  { color: #007020                      }  /* Name.Builtin */\r\n.syntax .nc  { color: #0e84b5; font-weight: bold   }  /* Name.Class */\r\n.syntax .no  { color: #60add5                      }  /* Name.Constant */\r\n.syntax .nd  { color: #555555; font-weight: bold   }  /* Name.Decorator */\r\n.syntax .ni  { color: #d55537; font-weight: bold   }  /* Name.Entity */\r\n.syntax .ne  { color: #007020                      }  /* Name.Exception */\r\n.syntax .nf  { color: #06287e                      }  /* Name.Function */\r\n.syntax .nl  { color: #002070; font-weight: bold   }  /* Name.Label */\r\n.syntax .nn  { color: #0e84b5; font-weight: bold   }  /* Name.Namespace */\r\n.syntax .nt  { color: #062873; font-weight: bold   }  /* Name.Tag */\r\n.syntax .nv  { color: #bb60d5                      }  /* Name.Variable */\r\n.syntax .ow  { color: #007020; font-weight: bold   }  /* Operator.Word */\r\n.syntax .sd  { color: #4070a0; font-style: italic  }  /* Literal.String.Doc */\r\n.syntax .se  { color: #4070a0; font-weight: bold   }  /* Literal.String.Escape */\r\n.syntax .si  { color: #70a0d0; font-style: italic  }  /* Literal.String.Interpol */\r\n.syntax .sx  { color: #c65d09                      }  /* Literal.String.Other */\r\n.syntax .sr  { color: #235388                      }  /* Literal.String.Regex */\r\n.syntax .ss  { color: #517918                      }  /* Literal.String.Symbol */']
