body{
  font-size:0.9em;
  background: {{ background_body }};
  margin: 15px 10% 15px 10%;
}

#headline {
  background: {{ background_headline }};
  padding: 10px;
  border: 1px solid black;
}

.main_menu {
  background: {{ background_mainmenu }};
  float: left;
  padding: 15px;
  margin: 10px;
}
.main_menu ul {
  padding: 0;
}
.main_menu ul ul {
  /* indent submenu */
  padding-left: 1.5em;
  font-size:0.9em; /* smaller font for subitems */
}
.main_menu li {
  list-style:none;
}
a {
  color: slategray;
  text-decoration: none;
}
a:hover, a .current {
  background-color: {{ link_current_background }};
  text-decoration: underline;
}
#main_content {
  background: {{ background_main_content }};
  padding: 5px;
  border: 1px solid black;
}

#footer {
  border: 1px solid {{ black }};
  padding: 5px;
  font-size: 0.9em;
  text-align: center;
}

/* ---------------------------------------------------------------------- */
textarea {
  width: 100%;
}
.back_links, #permalink {
  font-size: 0.8em;
}
#permalink {
  text-align: right;
  padding: 10px;
}
/* ----------------------------------------------------------------------
   Pygments syntax highlighter
---------------------------------------------------------------------- */
.pygments_code, .pygments_code table, .pygments_code .code, .pygments_code pre {
  background: {{ white }};
  padding: 0;
  margin: 0;
}
.pygments_code pre {
 border: 1px;
}
.pygments_code .code {
  width: 100%;
}

body {
  background-color:{{ main_color_body_background }};
}
.container {
  background-color:{{ main_color_content_background }};
  padding: 1em;
  margin: 20px auto;
  border: 4px double {{ main_color_4 }};
  border-radius: 20px;
  box-shadow: #aaa 0px 0px 15px;
  -webkit-box-shadow: #aaa 0px 0px 15px;
}
a {
  color: {{ main_color_link_normal }};
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
  color: {{ main_color_link_hover }};
}
#float_nav {
  width: 23em;
  float:right;
  background-color:{{ main_color_menu_background }};
  padding: 1em;
  margin: 0 0 1em 1em;
  border: 1px solid {{ main_color_1 }};
  border-radius: 10px;
  box-shadow: #aaa 2px 2px 4px;
  -webkit-box-shadow: #aaa 2px 2px 4px;
}
/* ----------------------------------------------------------------------------
   page messages
------------------------------------------------------------------------------- */
#page_msg {
  margin: 0;
  padding: 0 1em;
}

#page_msg ul {
  margin: 0;
  padding: 0;
  list-style-type:none;
}
/* ----------------------------------------------------------------------------
   main_menu
------------------------------------------------------------------------------- */
.main_menu *, .main_menu a.current {
  font-size:1em;
  font-family:monospace;
  font-weight: normal;
  text-decoration:none;
  list-style-type:none;
  padding: 0px;
  margin: 0px;
}
.main_menu {
  font-size: 1.5em;
  line-height: 1em;
}
.main_menu a.active {
  color: {{ darkslategrey_d40 }};
  font-weight: bold;
  letter-spacing: 1px;
}
.main_menu a.current {
  color: {{ main_color_2 }};
  background-color: {{ white }};
}
.main_menu a:before {
  letter-spacing: 0px;
  font-weight: bold;
  font-size:1.2em;
  background:{{ main_color_menu_background }};
  content:"\0251C  ";
}
.main_menu a.level_1:before { content:"\2502 \0251C  "; }
.main_menu a.level_2:before { content:"\2502 \2502 \0251C  "; }

.main_menu a.first:before { content:"\250c  "; }
.main_menu a.level_1.first:before { content:"\2502 \250c  "; }
.main_menu a.level_2.first:before { content:"\2502 \2502 \250c  "; }

.main_menu a.last:before { content:"\2514  "; }
.main_menu a.level_1.last:before { content:"\2502 \2514  "; }
.main_menu a.level_2.last:before { content:"\2502 \2502 \2514  "; }
/* ----------------------------------------------------------------------------
   search
------------------------------------------------------------------------------- */
.PyLucidPlugins.search #search_form input[type=text] {
  width: 17em;
}
.search input, .search button {
  font-size: 0.9em;
}
/* ----------------------------------------------------------------------------
   TOC Plugin
------------------------------------------------------------------------------- */
h3.toc_headline {
  margin: 1em 0 0 0;
}
.PyLucidPlugins.TOC {
  border: 1px solid {{ white }};
  background-color: {{ white }};
  margin: 3em;
  padding: 1em 0em 1em 0em;
}
.PyLucidPlugins.TOC h3 {
  margin: 0em;
  color: {{ darkslategrey_d40 }};
}
.PyLucidPlugins.TOC * {
  margin: 0em;
  color: {{ gainsboro }};
text-decoration:none;
}
.PyLucidPlugins.TOC a {
  color: {{ gray_d16 }};
}
.PyLucidPlugins.TOC a:hover {
  color: {{ darkred_d31 }};
}
/* ----------------------------------------------------------------------------
   Pygments source code
------------------------------------------------------------------------------- */
fieldset.pygments_code, fieldset.pygments_code * {
  padding: 0;
  margin: 0;
  vertical-align:top;
}
fieldset.pygments_code, legend.pygments_code {
  background: {{ white }};
  border: 1px solid {{ lightgray_d10 }};
}
fieldset.pygments_code {
  padding: 5px;
}
legend.pygments_code {
  font-size: 0.8em;
  color: {{ darkslategrey_d26 }};
  padding: 0px 5px 0px 5px;
  margin-left: 10px;
}
.pygments_code .linenos {
  background: {{ white }};
  color: {{ darkgrey_d28 }};
  padding: 0 5px 0 5px;
}
.pygments_code .code {
  width: 100%;
}
.pygments_code .code pre {
  width: 730px;
  overflow:auto;
}
/*.pygments_code .code pre:hover {
  width: 100%;
  overflow:show;
}*/
/* ----------------------------------------------------------------------------
   button links (edit/delete, retweer etc.)
------------------------------------------------------------------------------- */
.button_links a, .admin_links a {
  text-decoration:none;
  padding: 2px 5px 2px 5px;
  border-radius: 3px;
  -moz-border-radius: 3px;
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#dddddd));
  background: -moz-linear-gradient(top, #ffffff, #dddddd);  
  border: 1px solid {{ silver_d9 }};
}
.button_links a:hover, .admin_links a:hover {
  text-decoration:underline;
  text-shadow:1px 2px 2px {{ gray_d14 }};
}
/* ----------------------------------------------------------------------------
   Update journal
------------------------------------------------------------------------------- */
.PyLucidPlugins.update_journal * {
   white-space:nowrap;
}
.PyLucidPlugins.update_journal small {
   color: {{ darkgrey_d28 }};
}
.PyLucidPlugins.update_journal .update_time {
   color: {{ gray_d16 }};
}
.PyLucidPlugins.update_journal .update_title * {
   white-space:normal;
}
/* ----------------------------------------------------------------------------
   Headline Anchor
------------------------------------------------------------------------------- */
.top_link {
  color: {{ black }};
}
.top_link .uarr {
  color: {{ white }};
}
a.top_link {
  text-decoration:none;
}
.top_link:hover .uarr {
  color: {{ black }};
}
.section_anchor {
  text-shadow: 0px 0px {{ white }};
  text-decoration:none;
  color: {{ white }};
  padding-right: 1em;
}
.headline_anchor:hover .section_anchor {
  color: {{ darkgrey_d28 }};
}
.section_anchor:hover {
  color:#000000!important;
  text-decoration:underline;
  text-shadow: 2px 2px 2px {{ darkgrey_d28 }};
}
/* ----------------------------------------------------------------------------
   User comments
------------------------------------------------------------------------------- */
.PyLucidPlugins.pylucid_comments {
    padding: 3em 0 3em 0;
}
/* ----------------------------------------------------------------------------
   permalink
------------------------------------------------------------------------------- */
#permalink * {
  color: {{ gray }};
  padding: 0px;
  margin: 0px;
}
#permalink {
  font-size: 0.8em;
  text-align: center;
}
#permalink a {
  background-color: inherit;
  text-decoration: none;
}
#permalink a:hover {
  text-shadow:1px 1px 1px {{ gray_d14 }};
}
/* ----------------------------------------------------------------------------
   footer
------------------------------------------------------------------------------- */
.footer {
  text-align:center;
  border-top: 1px solid {{ white }};
}
.footer, .footer * {
  color: {{ gainsboro }};
}
/* ----------------------------------------------------------------------------
   lexicon
------------------------------------------------------------------------------- */
.PyLucidPlugins.lexicon dt {
  padding-top: 1.5em;
}
.PyLucidPlugins.lexicon dd {
  padding-top: 0.2em;
}
a.PyLucidPlugins.lexicon {
  color: {{ main_color_link_normal2 }};
  border-bottom: 1px dotted {{ lightgray_d12 }};
  font-weight: normal;
  text-shadow: 1px 1px 1px {{ white }};
}
a.PyLucidPlugins.lexicon:hover {
  color:{{ darkred_d31 }};
  font-weight: normal;
  text-decoration:underline;
  border-bottom: 0px;
  text-shadow: 2px 2px 2px {{ darkgrey }};
}
.PyLucidPlugins.lexicon .admin_links {
  float: right;
}
.PyLucidPlugins.lexicon .admin_links a {
  font-weight: normal;
}
.PyLucidPlugins.lexicon.openinwindow {
  cursor:help;
}
/* ----------------------------------------------------------------------------
   tag navigation
------------------------------------------------------------------------------- */
#tag_navi_lucidTag {
  font-size:0.8em;
}
/* ----------------------------------------------------------------------------
   internal
------------------------------------------------------------------------------- */
textarea {
  /* all text areas should use the full width */
  width: 100%;
}
.PyLucidPlugins.SiteMap p.deep_1 {
  border-bottom: 2px solid {{ darkred_d31 }};
}
/* ----------------------------------------------------------------------------
   RSS feeds included in the page
------------------------------------------------------------------------------- */
.PyLucidPlugins.rss h1 {
  font-size: 1.1em;
  padding: 1.5em 0em 0em 0em;
}
.PyLucidPlugins.rss * {
  line-height: 1.2em;
}
.PyLucidPlugins.rss ul {
  list-style:none;
  padding: 0px;
}
/* ----------------------------------------------------------------------------
   PyLucid blog plugin
------------------------------------------------------------------------------- */
.PyLucidPlugins.blog fieldset {
  border: 0px;
  border-top: 2px solid {{ lightgray_d12 }};
  padding: 0em 0em 2.5em 1em;
  margin: 2.5em 0em 0em 0em;
}
.PyLucidPlugins.blog legend {
  padding: 0em 0.3em 0em 0.3em;
}
.PyLucidPlugins.blog .tag_cloud {
  font-size: 0.7em;
}
.PyLucidPlugins.blog .headline {
  font-size: 1.6em;
  line-height: 0.7em;
}
.PyLucidPlugins.blog .headline_info {
  font-size: 0.45em;
  color: {{ dimgrey_d35 }};
}
/* ----------------------------------------------------------------------------
   PyLucid language plugin
------------------------------------------------------------------------------- */
.PyLucidPlugins.language * {
  color: {{ gray_d16 }};
  display: inline;
  padding: 0em;
  margin: 0em;
}
.PyLucidPlugins.language {
  border: 1px solid {{ gainsboro }};
  background-color: {{ white }};
  padding: 1px;
  margin: 3px;
}
.PyLucidPlugins.language:hover {
  border-color: {{ main_color_4 }};
}
.PyLucidPlugins.language:hover * {
  color: {{ black }};
}
.PyLucidPlugins.language li a:hover {
  color: {{ main_color_4 }};
}
.PyLucidPlugins.language li {
  border-right: 2px solid {{ darkgrey }};
  padding: 0em 0.2em 0em 0.4em;
}
.PyLucidPlugins.language li:last-child {
  border: 0px;
}

