/***** Body and main container *****/
body {
   margin:0 auto;
   padding:0;
   font:76% Verdana,Tahoma,Arial,sans-serif;
   background:{{ grey_white_5 }};
   color:{{ grey_dark_1 }};
}

#wrap {
   background:{{ white }};
   color:{{ grey_dark_1 }};
   margin:0 auto;
   width:760px;
}

/***** Various tags *****/
a {
   text-decoration:none;
   font-weight:bold;
   color:{{ link_normal }};
   background-color:inherit;
}
a:hover {
   text-decoration:underline;
   color: {{ link_hover }};
   background-color:inherit;
}
a img {
   border:0;
}

/***** Header *****/
#header {
   background-color:{{ grey_white_3 }};
   margin:10px 0 0;
   padding:0;
   height:45px;
}
#header h1 {
   color:{{ grey_dark_2 }};
   background-color:inherit;
   width:250px;
   margin:0 0 10px;
   font-size:2.2em;
   float:left;
}
#header h1 a{
   color:{{ grey_dark_2 }};
   background-color:inherit;
   text-decoration:none;
}
#header p {
   width:500px;
   float:right;
   text-align:center;
   color:{{ grey_dark_2 }};
   margin:8px 0 0;
   font-size:1.1em;
   font-weight:bold;
   line-height:1.3em;
   background-color:inherit;
}

#frontphoto {
   margin:0 0 10px;
   border:0;
}

/***** Main menu *****/
.main_menu ul {
   width:140px;
   margin:0 0 18px;
   padding:0;
   list-style:none;
}
.main_menu li {
   width:140px;
   padding:0;
   line-height:1.4em;
   display:inline;
}
.main_menu li a {
   background-color:{{ grey_white_1 }};
   color:{{ grey_dark_2 }};
   font-weight:bold;
   width:130px;
   float:left;
   margin-bottom:5px;
   padding:5px 1px 5px 5px;
   border-left:4px solid {{ grey_dark_4 }};
   text-decoration:none;
}
.main_menu li a:hover {
   background-color:{{ grey_white_3 }};
   border-left:4px solid {{ link_normal }};
}
.main_menu li a.active {
