   font-size:0.9em;
   background-color:{{ grey_white_1 }};
   color:{{ grey_dark_2 }};
   border-left:4px solid {{ grey_dark_4 }};
   line-height:1.5em;
}
#leftside div.announce p {
   margin:10px 0 0;
   padding:0;
}

/***** Right sidebar *****/

#rightside {
   float:right;
   width:140px;
   margin:0 0 5px;
   padding:0;
   line-height:1.4em;
}

#extras p,#extras ul {
   font-size:0.9em;
   margin:0 0 16px;
   padding:0;
}
#extras li {
   list-style:none;
   line-height:1.2em;
   margin:0 0 6px 0;
   padding:0;
