#extras ul ul li a {
   font-size:1.1em;
   font-weight:normal;
}
#extras h2, #extras h3 {
   color:{{ grey_dark_2 }};
   background-color:inherit;
   font-size:1.2em;
   margin:0 0 10px;
   padding:0;
}

/***** Main content *****/
#content {
   margin:0 200px 5px 200px;
   border-left:1px solid {{ grey_white_4 }};
   border-right:1px solid {{ grey_white_4 }};
   padding:0 10px;
   line-height:1.6em;
}
#contentwide {
   margin:0 0 5px 200px;
   border-left:1px solid {{ grey_white_2 }};
   padding:0 0 0 10px;
   line-height:1.6em;
}
#content h2,#contentwide h2 {
   font-size:1.6em;
   margin:0 0 10px;
}
#content h3,#contentwide h3 {
   font-size:1.4em;
   margin:0 0 8px;
}
#content img.normal, #contentwide img.normal {
   padding:1px;
   display:inline;
   background:{{ grey_dark_4 }};
   color:{{ grey_dark_1 }};
   border:4px solid {{ grey_white_2 }};
}
#content ul, #contentwide ul, #content ol, #contentwide ol {
   margin:0 0 16px 20px;
   padding:0;
}
#content li, #contentwide li {
   margin:0;
   padding:0 0 0 5px;
}
#content ul ul, #contentwide ul ul, #content ol ol, #contentwide ol ol{
   margin:0 0 0 16px;
   padding:0;
}

/***** Footer *****/
#footer {
   clear:both;
   margin:0 auto;
   padding:10px 0 20px;
   border-top:4px solid {{ grey_white_2 }};
   width:760px;
   text-align:center;
   color:{{ grey_dark_3 }};
   background:{{ white }};
   font-size:0.9em;
}
#footer a {
   color:{{ grey_dark_3 }};
   background-color:inherit;
   text-decoration:none;
}
#footer a:hover {
   text-decoration:underline;
}

/***** Various classes *****/
.left {
   margin:3px 10px 2px 0;
   float:left;
}
.right {
   margin:2px 0 2px 10px;
   float:right;
}

.textright {
   text-align:right;
}

.center {
   text-align:center;
}

.small {
   font-size:0.8em;
}

.bold {
   font-weight:bold;
}

.hide {
   display:none;
}

