   <h1 id="headline"><a href="/">PyLucid CMS</a></h1>
</div>

<div class="span-24 last">   
   {% lucidTag admin_menu %}
</div>
       
<div class="span-24 last" id="page_content">
   <div id="float_nav">
      {% lucidTag language %}
      {% lucidTag main_menu %}
      {% lucidTag search %}
   </div>
   <h2 id="page_title">{{ page_title|escape }}</h2>
   <!-- ContextMiddleware breadcrumb -->
   {% include "pylucid/includes/page_msg.html" %}      
   {% lucidTag TOC min=3 %}
   {% block content %}{{ page_content }}{% endblock content %}
   {% lucidTag pylucid_comments %}
</div>
   
<div class="span-20">
    {% lucidTag tag_navi %}
